        <x-calendar />
        <div class="bg-white rounded-xl border border-l-[25px] border-l-contact-bg flex gap-2 items-center pl-2 py-2">
            <p class="font-normal text-gray-title text-2xl">2</p>
            <span class="text-sm">Post publicados este mes</span>
        </div>
        <div class="bg-white rounded-xl border border-l-[25px] border-l-blue-custom flex gap-2 items-center pl-2 py-2">
            <p class="font-normal text-gray-title text-2xl">3</p>
            <span class="text-sm">Post programados para el resto del mes</span>
        </div>
        <button
        class="bg-blue-custom hover:backdrop-brightness-50 text-white text-center rounded-md py-3 px-3 transition-colors w-full text-sm font-semibold leading-3">Ver mi planificación</button>
    </section>
</section>
@section('scripts')
    <script>
        var swiper = new Swiper(".multiple-carousel", {
            loop: true,
            slidesPerView: 3,
            spaceBetween: 10,
            navigation: {
                prevEl: ".prev",
                nextEl: ".next",
            },
            breakpoints: {
                1920: {
                    slidesPerView: 5,
                    spaceBetween: 10
                },
                1028: {
                    slidesPerView: 4,
                    spaceBetween: 10
                },
                990: {
                    slidesPerView: 3,
                    spaceBetween: 0
                }
            }
        });
    </script>

@endsection
